import XCTest
@testable import StashKit


final class HashTests: XCTestCase {
    func testSha256Sum() throws {
        // This is an example of a functional test case.
        // Use XCTAssert and related functions to verify your tests produce the correct
        // results.
        
        guard let data = Data(base64Encoded: """

""") else { return }
        
        let dataManager = FileDataManager()

        XCTAssertEqual(dataManager.calcChecksum(data: data), "37c2d60e97ff09def5bb7039ff2f0f922e7f600ac95a00bd88d3f8fb8090f7b0")
    }
}
